import React, {useState} from "react";
import ExpenseForm from "./ExpenseForm";
import "./NewExpense.css";

const NewExpense = (props) => {

  const [showHideFormContent,setShowHideFormContent]=useState("false");
  const saveExpenseDataHandler = (enteredExpenseData) => {
    const expenseData = {
      ...enteredExpenseData,
      id: Math.random().toString()
    };
    props.onAddExpense(expenseData);
  };

  const cancelHandler =(showExpenseFormContent)=>{
    if (showExpenseFormContent=="true"){
      console.log("Show content")
    }
    else{
      console.log("Do not show contents")
    }
   
  };
  return (
    <div className="new-expense">
      <ExpenseForm onSaveExpenseData={saveExpenseDataHandler} onCancel={cancelHandler} />
    </div>
  );
};

export default NewExpense;
