import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpensesFilter";
import "../Expenses/Expenses.css";
import Card from "../UI/Card";
import { useState } from "react";

function Expenses(props) {
  //const expenses = props.expenses;
  //let expenses = props.expenses;

  const [selectedYear, setselectedYear] = useState("2020");

  const expenseFilterYearChangeHandler = (expFilterDateYear) => {
    setselectedYear(expFilterDateYear);
    // console.log(expFilterDateYear);
    console.log(props.expenses.date);
    // expenses= props.expenses.filter((selectedYear) => {props.expenses.date.year == selectedYear});
  };

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={selectedYear}
          onExpenseFilterYearChange={expenseFilterYearChangeHandler}
        />
        {expenses
        .filter((expense)=> {return expense.date.year==selectedYear})
          .map((expense) => (
            <ExpenseItem
              key={expense.id}
              title={expense.title}
              amount={expense.amount}
              date={expense.date}
            />
          ))}
      </Card>
    </div>
  );
}

export default Expenses;
